    path = "/home/sandboxes/pmargani/dss/nell/tools/"
    fullpath = path + filename
    f = open(fullpath, 'r')
    lines = f.readlines()

    for l in lines:
        # skip the comments
        if l[0] != "#":
            parts = l.split(",")
            name = parts[0]
            dict[name] = l

    return dict        
        

